mapper.select:
SELECT 
FROM cdms_delivery_order

    deliveryman_id = #{userId,jdbcType=BIGINT}
    AND status = #{status,jdbcType=TINYINT}

        AND (order_id LIKE CONCAT('%', #{keyword}, '%')
        OR consignee_name LIKE CONCAT('%', #{keyword}, '%'))


ORDER BY create_time DESC
LIMIT #{start,jdbcType=INTEGER}, #{size,jdbcType=INTEGER}

SELECT d.*, u.username AS delivery_name FROM cdms_delivery_order d LEFT JOIN cdms_user u ON d.deliveryman_id = u.user_id
AND d.status = #{status,jdbcType=TINYINT} AND d.deliveryman_id = #{userId,jdbcType=BIGINT}
AND ( d.order_id LIKE CONCAT('%', #{keyword}, '%') OR d.sender_name LIKE CONCAT('%', #{keyword}, '%') OR d.sender_phone LIKE CONCAT('%', #{keyword}, '%') OR d.consignee_name LIKE CONCAT('%', #{keyword}, '%') OR d.consignee_phone LIKE CONCAT('%', #{keyword}, '%') OR u.username LIKE CONCAT('%', #{keyword}, '%') )
ORDER BY d.create_time DESC LIMIT #{start,jdbcType=INTEGER}, #{size,jdbcType=INTEGER}

mapper.update:
UPDATE cdms_delivery_order
SET
    deliveryman_id = #{deliverymanId,jdbcType=BIGINT},
    status = #{targetStatus,jdbcType=TINYINT}
WHERE order_id = #{orderId,jdbcType=VARCHAR}
  AND status = #{oldStatus,jdbcType=TINYINT}
  AND deliveryman_id IS NULL

UPDATE cdms_delivery_order
SET
status = #{targetStatus,jdbcType=TINYINT},

    complete_time = #{updateTime,jdbcType=TIMESTAMP}  
    cancel_time = #{updateTime,jdbcType=TIMESTAMP}    

WHERE order_id = #{orderId,jdbcType=VARCHAR}

mapper.select:
SELECT COUNT(*) FROM cdms_delivery_order
AND status = #{status,jdbcType=TINYINT}
AND (order_id LIKE CONCAT('%', #{keyword}, '%') OR consignee_name LIKE CONCAT('%', #{keyword}, '%'))

SELECT 
FROM cdms_delivery_order
WHERE
deliveryman_id = #{deliverymanId,jdbcType=BIGINT}
AND status = #{status,jdbcType=TINYINT}
ORDER BY create_time DESC

SELECT 
FROM cdms_delivery_order
WHERE
deliveryman_id = #{deliverymanId,jdbcType=BIGINT}
AND status = #{status,jdbcType=TINYINT}
ORDER BY create_time DESC

SELECT 
FROM cdms_delivery_order

    AND status = #{status,jdbcType=TINYINT}

        AND (order_id LIKE #{keyword,jdbcType=VARCHAR}
        OR sender_name LIKE #{keyword,jdbcType=VARCHAR}
        OR consignee_name LIKE #{keyword,jdbcType=VARCHAR})


ORDER BY create_time DESC
LIMIT #{start,jdbcType=INTEGER}, #{size,jdbcType=INTEGER}
