mapper.select:
SELECT 
FROM cdms_operation_log
WHERE operator_id = #{operatorId,jdbcType=BIGINT}
AND operation_time BETWEEN #{startTime,jdbcType=TIMESTAMP} AND #{endTime,jdbcType=TIMESTAMP}
ORDER BY operation_time DESC
