mapper.update:
UPDATE cdms_delivery_order
SET
status = #{targetStatus,jdbcType=INTEGER}

    , complete_time = NOW()  


    , cancel_time = NOW()   

WHERE
order_id = #{orderId,jdbcType=VARCHAR}
AND status IN (  
1,2  
0,1,2 
${status}  
)

mapper.select:
SELECT order_id, sender_name, sender_phone, sender_address, consignee_name, consignee_phone, consignee_address, goods_type, weight, volume, delivery_fee, platform_income, deliveryman_income, expected_mins, remark, status, create_time, creator_id, deliveryman_id, complete_time, cancel_time FROM cdms_delivery_order
AND status = #{status,jdbcType=INTEGER}
AND ( order_id LIKE CONCAT('%', #{keyword,jdbcType=VARCHAR}, '%') OR sender_name LIKE CONCAT('%', #{keyword,jdbcType=VARCHAR}, '%') OR consignee_name LIKE CONCAT('%', #{keyword,jdbcType=VARCHAR}, '%') )
ORDER BY create_time DESC LIMIT #{start,jdbcType=INTEGER}, #{size,jdbcType=INTEGER}
